CREATE TABLE Users( User_Id SERIAL NOT NULL PRIMARY KEY ,Email_Id varchar(50) Not NULL,Password varchar(1000) NOT NULL,First_Name varchar(250) Not NULL,Last_Name varchar(250) NOT NULL,Created_on timestamp without time zone NOT NULL,Created_by varchar not null, Updated_on timestamp without time zone NULL,Updated_by varchar null,Role varchar(20)NOT NULL,Teams Varchar Not NULL,Delete BIT null);
CREATE TABLE Asset(Asset_Id  SERIAL NOT NULL PRIMARY KEY,Asset_Name varchar (50) NOT NULL,Manufacturer varchar NOT NULL, BMC_IP varchar NOT NULL, BMC_User varchar NOT NULL, BMC_Password varchar NOT NULL, Asset_Location varchar NOT NULL,Reserved BOOL, Assigned_to INT,FOREIGN KEY(Assigned_to) REFERENCES Users(User_Id) ,Assigned_from timestamp without time zone null,Assigned_by varchar(50)  null,Created_on timestamp without time zone NOT NULL,Created_by VARCHAR(50)  NULL,Updated_on timestamp without time zone NULL,Updated_by varchar  NULL,OS_IP inet null,OS_User SERIAL  NOT NULL,FOREIGN KEY(OS_User) REFERENCES Users(User_ID) ,OS_Password varchar(1000) null, Purpose varchar(300) NOT NULL,Cluster_Id  Varchar Not NULL, Delete BIT NULL,Status BOOL  NULL );
CREATE TABLE Historic_details(Id SERIAL NOT NULL  PRIMARY KEY,Asset_Id SERIAL NOT NULL ,Assigned_to INT,FOREIGN KEY(Assigned_to) REFERENCES Users(User_Id),FOREIGN KEY(Asset_Id) REFERENCES Asset(Asset_Id),Assigned_from timestamp without time zone NOT NULL,Updated_on timestamp without time zone  NULL,Updated_by varchar not null,Remarks varchar(300)NOT NULL);
CREATE TABLE Platform_Profile(Id SERIAL NOT NULL  PRIMARY KEY,Asset_Id SERIAL NOT NULL,FOREIGN KEY(Asset_Id) REFERENCES Asset(Asset_Id),Ram_gb INT NOT NULL,Storage_gb INT NOT NULL,Cpu_model VARCHAR(100) NOT NULL);
CREATE TABLE Server_Request(Id SERIAL NOT NULL PRIMARY KEY,Creator varchar NOT NULL, Start_Date timestamp without time zone NOT NULL,End_Date timestamp without time zone NOT NULL,Manufacturer varchar(50) NOT NULL,Number_Of_Servers INT NOT NULL,Cpu_model varchar(50)NOT NULL,CPU_Sockets INT NOT NULL,DIMM_Size Varchar(50)NOT NULL,DIMM_Quantity INT NOT NULL,OS_Vendor Varchar(50) NOT NULL,OS_Controller Varchar(50)NOT NULL,OS_Capacity varchar(50)NOT NULL,Disk_Vendor Varchar(50) NOT NULL,Disk_Controller Varchar(50)NOT NULL,Disk_Capacity varchar(50)NOT NULL,Network_Type BOOL NOT NULL,Network_speed varchar(50)NOT NULL,Network_ports INT NOT NULL,Special_Switching_Needs Varchar(100),Infraadmin_Comments TEXT[] null,User_Comments TEXT[] null,Request bool);



    



